import org.mule.galaxy.event.*;import org.mule.galaxy.event.annotation.*;// An event listener which fires emails when new entry versions are created@BindToEvent("PropertyChanged")public class ConsoleLifecycleNotifier {    @Async    @OnEvent    void onEvent(PropertyChangedEvent e) {        if ("primary.lifecycle" == e.propertyName)            println "Got lifecycle transition event for ${e.itemPath}"    }}// remove the previous listener, if anyeventManager.listeners.each {    if (it.class.simpleName == "ConsoleLifecycleNotifier") {        eventManager.removeListener(it)        println "Removed listener"    }}// add the event listener againeventManager.addListener(new ConsoleLifecycleNotifier())